% Sistem inferior triunghiular% cu vectorizare% O(n^2)function[x] = SIT(A, b)  n = length(A); % se ia cea mai mare dim a lui A  x = zeros(n, 1); % initializam vectorul rezultat  for i = [1:n]    s = A(i, 1:i-1) * x(1:i-1); % in cazul for-ului, aveam k=1:i-1    x(i) = (b(i) - s) / A(i, i);  endforendfunction% Verif:% Iau o matr A; A = tril(A); iau un vector b; A * x = b ?