% Derivare numerica% Metoda Taylor de ord 1 / Metoda Eulerfunction [values] = Taylor_1(df, t0, x0, h, N)    % Vom initializa un vector cu toate valorile pe care    % le aproximam ale functiei x cu zerouri si    % stim ca sunt N + 1, de la 0 la N    values = zeros(N + 1, 1);    % Prima valoare este cea pe care o primim, x0, cea in t0    values(1) = x0;        for i = [2:N+1]      % tk este prev_t + h; xi -> x(t0 + i*h)      % (i - 2) pt ca am inceput de la 2      tk = t0 + h * (i - 2);      % xk o sa fie x0      xk = values(i - 1);      % Aplic formula      values(i) = xk + h * df(tk, xk);    endforendfunction% Testare:# function [y] = df(t, x)# y = -x + exp(-t);# endfunction% Apelare:# res_1 = Taylor_1(@df, 0, 0, 0.1, 30)